import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Logo from "./asset/logo.png";


function Navbar()  {
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobilemenu = () => setClick(false)

  let [account, setAccount] = useState("");
    let[isConnected, setIsConnected] = useState(false);

  const{ ethereum } = window;
  const connectMetamask = async () => {
    if (window.ethereum !== "undefined") {
        try {
        const accounts = await ethereum.request({method: "eth_requestAccounts"});
        setAccount(accounts[0]);
        setIsConnected(true);
        }
        catch (error) {
            console.error("Error connecting Metamask:", error);
        }
    }
        else {
            console.warn("Metamask not available"); 
        }
    }



  const disconnectMetamask  = () => {
    setAccount("")
    setIsConnected(false);
      }

  return (
    <>
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo" onclick={closeMobilemenu}>
          <img src={Logo} alt="logo" />
        </Link>

        <div className="menu-icon" onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        </div>

        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li className="nav-item">
            <Link to="/" className="nav-links" onClick={closeMobilemenu}>
              Marketplace
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/" className="nav-links" onClick={closeMobilemenu}>
              Learn
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/" className="nav-links" onClick={closeMobilemenu}>
              FAQs
            </Link>
          </li>
          <div className="navbar-user">
            <li className="users">
              <Link to="/" onClick={closeMobilemenu}>
              <p>{account}</p>
                            {isConnected ? (
                            <span className="btn-grad" role="button"onClick={disconnectMetamask}>Disconnect Wallet</span>
                            ) : (
                            <span className="btn-grad" role="button"onClick={connectMetamask}>Connect Wallet</span>)}

              </Link>
            </li>
            <li className="users">
              <Link to="/" onClick={closeMobilemenu}>
                <span className="btn-grad" role="button">Contact</span>
              </Link>
            </li>
          </div>
        </ul>
      </div>
    </nav>


    </>
  )
}

export default Navbar
